
public class driver {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] alwaysContinues = {"A", "L", "W", "A", "Y", "S", "C", "O", "N", "T", "I", "N", "U", "E", "S"};
		
		for (int i = 0; i < alwaysContinues.length; ++i) {
			System.out.println(alwaysContinues[i]);
		}
		System.out.println("It always continues!");
	}

}
